 .equ distance, 256
 .equ offset, 32
 .equ strincr, 4
 // pre-load 15 twiddle factors to 15 FPU registers
 // s0-s7 used to temporary store 16 16-bit polys.
 vldm twiddle_ptr!, {s8-s22}

 add tmp, poly, #strincr*8
 // s23: poly addr
 // s24: tmp
 vmov s24, tmp
